class InstrumentoMusical {
  modelo;
  marca;
  tipo;
  cor;
  circuito;
  capa;
  marcaDaCapa;
  corda;
  marcaDaCorda;

  constructor(
    modelo,
    marca,
    tipo,
    cor,
    circuito,
    capa,
    marcaDaCapa,
    corda,
    marcaDaCorda
  ) {
    this.modelo = modelo;
    this.marca = marca;
    this.tipo = tipo;
    this.cor = cor;
    this.circuito = circuito;
    this.capa = capa;
    this.marcaDaCapa = marcaDaCapa;
    this.corda = corda;
    this.marcaDaCorda = marcaDaCorda;
  }
}
